
/**
 * @param {string} s
 * @param {string} t
 * @return {boolean}
 */
var isSubsequence = function(s, t) {
    let sLength = s.length
    let tLength = t.length
    if (sLength === 0 && tLength === 0) {
        return true
    }
    if (tLength === 0) {
        return false
    }
    let flag = 0
    for (let i = 0; i < sLength; i++) {
        let j = flag
        if (flag >= tLength) {
            return false
        }
        let target = s[i]
        console.log(target)
        while(j < tLength) {
            console.log('t----', t[j])
            if (target === t[j]) {
                flag = j + 1
                break
            }
            // if we met the last one, it says there's no sub sequence in t
            if (j === tLength - 1) {
                return false
            }
            j++
        }
    }
    // if we can reach the last one of s and still find char in t
    // then return true
    return true
};
let s = "acb", t = "ahbgdc"
// let s = "bcd"
// let t = ""


console.log(isSubsequence(s, t))